<script>

export default {
  methods: {
    handleScroll(event) {
      if (window.scrollY > 10) {
        this.$refs['navBar'].classList.add('floatingNav')
      } else {
        this.$refs['navBar'].classList.remove('floatingNav')
      }
    }
  },

  mounted() {
    window.addEventListener('scroll', this.handleScroll);
  }
