<style>
  #container {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 200%;
  }

  #navBar {
    position: fixed;
    width: 100vw;
    height: 50px;
    background-color: #DEDEDE;
    transition: 0.2s;
    display: flex;
  }

  .floatingNav {
    width: calc(100vh - 100px);
    border-radius: 2px;
    box-shadow: 0px 1px 10px #999;
